// BookStore.js
import React, { useState } from 'react';

const BookStore = ({ name, books }) => {
  const [selectedBook, setSelectedBook] = useState('');

  // Extract unique books from all stores
  const uniqueBooks = Array.from(new Set(books.map(book => book.bookName)));

  const handleBookChange = (event) => {
    setSelectedBook(event.target.value);
  };

  return (
    <div className="book-store">
      <h2>{name}</h2>
      <select value={selectedBook} onChange={handleBookChange}>
        <option value="">Select a book...</option>
        {uniqueBooks.map((bookName, index) => {
          const book = books.find(book => book.bookName === bookName);
          return (
            <option key={index} value={book.bookName}>
              {book.bookName} - ${book.cost}
            </option>
          );
        })}
      </select>
      {/* Other book store details or components */}
    </div>
  );
};

export default BookStore;
