import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [selectedBook, setSelectedBook] = useState('');
  const [selectedType, setSelectedType] = useState('virtual');
  const [bestDeal, setBestDeal] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const findBestDeal = async () => {
    try {
      if (!selectedBook) {
        setErrorMessage('Please select a book');
        return;
      }

      const response = await axios.get(`http://localhost:8086/virtualstore/book?name=${encodeURIComponent(selectedBook)}&model=${selectedType}`);
      const data = response.data;
      setBestDeal(data); // Store the entire response in state
      setErrorMessage(''); // Clear error message on successful fetch
    } catch (error) {
      if (error.response) {
        console.error('Error response from server:', error.response.data);
        setErrorMessage(`Error: ${error.response.data.message}`);
      } else if (error.request) {
        console.error('No response received:', error.request);
        setErrorMessage('No response received from server');
      } else {
        console.error('Error setting up request:', error.message);
        setErrorMessage('Error setting up request');
      }
    }
  };

  return (
    <div>
      <h1>Find Best Deal</h1>
      <div>
        <label>Select Book:</label>
        <select onChange={(e) => setSelectedBook(e.target.value)}>
          <option value="">Select a book</option>
          {/* Add options dynamically */}
        </select>
      </div>
      <div>
        <label>Select Type:</label>
        <select onChange={(e) => setSelectedType(e.target.value)}>
          <option value="virtual">Virtual</option>
          <option value="traditional">Traditional</option>
        </select>
      </div>
      <button onClick={findBestDeal}>Find Best Deal</button>
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      {bestDeal && (
        <div>
          <h2>Best Deal</h2>
          <p>
            Book Name: {bestDeal.bestPriceDeal.bookName}
            <br />
            Author: {bestDeal.bestPriceDeal.author}
            <br />
            Store: {bestDeal.bestPriceDeal.bookStore}
            <br />
            Price: ${bestDeal.bestPriceDeal.cost}
          </p>
          <h3>All Deals</h3>
          <ul>
            {bestDeal.allDeals.map((deal, index) => (
              <li key={index}>
                <strong>{deal.bookStore}</strong>: {deal.cost}
              </li>
            ))}
          </ul>
          <h3>Time Statistics</h3>
          <ul>
            {Object.entries(bestDeal.timeStatistics.timeMap).map(([key, value], index) => (
              <li key={index}>
                <strong>{key}</strong>: {value} ms
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
