import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import BookStore from './components/BookStore';
import Footer from './components/Footer';
import axios from 'axios';
import Modal from 'react-modal';

const App = () => {
  const [bookStores, setBookStores] = useState([]);
  const [selectedBook, setSelectedBook] = useState('');
  const [bestDeal, setBestDeal] = useState(null);
  const [allBooks, setAllBooks] = useState([]);

  useEffect(() => {
    fetchBookStores();
  }, []);

  const fetchBookStores = async () => {
    try {
      const responses = await Promise.all([
        axios.get('https://7cf6495c-0e1d-45a7-9c82-1387408c7f3b.mock.pstmn.io/store/book'),
        axios.get('https://e5646159-afea-46da-a5a7-ff5759f70858.mock.pstmn.io/store/book'),
        axios.get('https://d94374a3-e1b3-464d-b609-ed7e45a732fa.mock.pstmn.io/store/book'),
        axios.get('https://6410a146-4160-4128-9806-b7f5329077b5.mock.pstmn.io/store/book')
      ]);

      const allBooks = responses.flatMap(response => response.data);
      const uniqueBooks = [...new Set(allBooks.map(book => book.bookName))];

      setAllBooks(uniqueBooks);
      const groupedBooks = groupBooksByStore(allBooks);
      setBookStores(groupedBooks);
    } catch (error) {
      console.error('Error fetching book stores:', error);
    }
  };

  const groupBooksByStore = (books) => {
    const grouped = {};
    books.forEach(book => {
      if (!grouped[book.bookStore]) {
        grouped[book.bookStore] = [];
      }
      grouped[book.bookStore].push(book);
    });
    return Object.keys(grouped).map(storeName => ({ name: storeName, books: grouped[storeName] }));
  };

  const handleBookChange = (event) => {
    setSelectedBook(event.target.value);
  };

  const findBestDeal = async () => {
    try {
      if (!selectedBook) {
        console.error('Please select a book');
        return;
      }

      const response = await axios.get(`http://localhost:8086/virtualstore/book`, {
        params: { name: selectedBook }
      });
      setBestDeal(response.data);
    } catch (error) {
      console.error('Error fetching best deal:', error);
    }
  };

  return (
    <div>
      <Header />
      <div className="book-stores">
        {bookStores.map((store, index) => (
          <BookStore key={index} name={store.name} books={store.books} />
        ))}
      </div>
      <div className="book-selection">
        <select value={selectedBook} onChange={handleBookChange} className="book-dropdown">
          <option value="">Select a book...</option>
          {allBooks.map((book, index) => (
            <option key={index} value={book}>{book}</option>
          ))}
        </select>
        <button onClick={findBestDeal} className="best-deal-button" disabled={!selectedBook}>
          Best Deal Price
        </button>
      </div>
      {bestDeal && (
        <Modal
          isOpen={!!bestDeal}
          onRequestClose={() => setBestDeal(null)}
          contentLabel="Best Deal"
          className="best-deal-modal"
          overlayClassName="best-deal-overlay"
        >
          <h2>Best Deal</h2>
          <p><strong>Book Name:</strong> {bestDeal.bestPriceDeal.bookName}</p>
          <p><strong>Author:</strong> {bestDeal.bestPriceDeal.author}</p>
          <p><strong>Store:</strong> {bestDeal.bestPriceDeal.bookStore}</p>
          <p><strong>Price:</strong> ${bestDeal.bestPriceDeal.cost}</p> 
          <h3>All Deals</h3>
          <ul>
            {bestDeal.allDeals.map((deal, index) => (
              <li key={index}>
                <strong>{deal.bookStore}</strong>: ${deal.cost} ({deal.bookName} by {deal.author})
              </li>
            ))}
          </ul>
          <button onClick={() => setBestDeal(null)}>Close</button>
        </Modal>
      )}
      <Footer />
    </div>
  );
};

export default App;
