import React from 'react';
import Header from './components/Header';
import BookStore from './components/BookStore';
import Footer from './components/Footer';

const bookStores = [
  {
    name: "Pathshala",
    books: [
      { bookName: "The Lord of the Rings", author: "J.R.R. Tolkien", cost: 320 },
      { bookName: "Pride and Prejudice", author: "Jane Austen", cost: 180 },
      { bookName: "To Kill a Mockingbird", author: "Harper Lee", cost: 245 },
      { bookName: "The Adventures of Huckleberry Finn", author: "Mark Twain", cost: 138 },
      { bookName: "The Martian", author: "Andy Weir", cost: 210 },
      { bookName: "Animal Farm", author: "George Orwell", cost: 142 },
      { bookName: "Things Fall Apart", author: "Chinua Achebe", cost: 275 },
      { bookName: "The Handmaid's Tale", author: "Margaret Atwood", cost: 199 },
      { bookName: "The Count of Monte Cristo", author: "Alexandre Dumas", cost: 350 },
      { bookName: "The Hitchhiker's Guide to the Galaxy", author: "Douglas Adams", cost: 167 }
    ]
  },
  {
    name: "Libra Book Store",
    books: [
      { bookName: "The Lord of the Rings", author: "Michael Connelly", cost: 15 },
      { bookName: "To Kill a Mockingbird", author: "Harper Lee", cost: 18 },
      { bookName: "1984", author: "George Orwell", cost: 12 },
      { bookName: "The Great Gatsby", author: "F. Scott Fitzgerald", cost: 14 },
      { bookName: "Moby Dick", author: "Herman Melville", cost: 17 },
      { bookName: "War and Peace", author: "Leo Tolstoy", cost: 20 },
      { bookName: "Pride and Prejudice", author: "Jane Austen", cost: 16 },
      { bookName: "The Catcher in the Rye", author: "J.D. Salinger", cost: 13 }
    ]
  },
  {
    name: "BookWorm Book Store",
    books: [
      { bookName: "The Lord of the Rings", author: "J.R.R. Tolkien", cost: 28 },
      { bookName: "Pride and Prejudice", author: "Jane Austen", cost: 15 },
      { bookName: "To Kill a Mockingbird", author: "Harper Lee", cost: 21 },
      { bookName: "The Catcher in the Rye", author: "J.D. Salinger", cost: 11 },
      { bookName: "The Martian", author: "Andy Weir", cost: 19 },
      { bookName: "Animal Farm", author: "George Orwell", cost: 12 },
      { bookName: "The Book Thief", author: "Markus Zusak", cost: 16 },
      { bookName: "The Handmaid's Tale", author: "Margaret Atwood", cost: 18 },
      { bookName: "The Count of Monte Cristo", author: "Alexandre Dumas", cost: 23 },
      { bookName: "The Hitchhiker's Guide to the Galaxy", author: "Douglas Adams", cost: 14 },
      { bookName: "Little Women", author: "Louisa May Alcott", cost: 17 },
      { bookName: "The Great Gatsby", author: "F. Scott Fitzgerald", cost: 10 },
      { bookName: "The Three Musketeers", author: "Alexandre Dumas", cost: 20 },
      { bookName: "Invisible Man", author: "Ralph Ellison", cost: 22 },
      { bookName: "One Hundred Years of Solitude", author: "Gabriel García Márquez", cost: 15 },
      { bookName: "Frankenstein", author: "Mary Shelley", cost: 13 },
      { bookName: "The Adventures of Sherlock Holmes", author: "Arthur Conan Doyle", cost: 18 },
      { bookName: "Jane Eyre", author: "Charlotte Brontë", cost: 11 }
    ]
  }
];

const App = () => {
  return (
    <div>
      <Header />
      <div className="book-stores">
        {bookStores.map((store, index) => (
          <div key={index}>
            <BookStore name={store.name} books={store.books} />
            <button>Best Deal Price</button>
          </div>
        ))}
      </div>
      <Footer />
    </div>
  );
};

export default App;
