import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './index.css'; // Ensure it imports index.css

const BookStore = ({ storeName, books, onBestDealClick }) => {
  const [showBooks, setShowBooks] = useState(false);

  const toggleBooks = () => {
    setShowBooks(!showBooks);
  };

  return (
    <div className="book-store">
      <h2 onClick={toggleBooks}>{storeName}</h2>
      {showBooks && (
        <ul>
          {books.map((book, index) => (
            <li key={index}>
              <p>Book Name: {book.bookName}</p>
              <p>Author: {book.author}</p>
              <p>Cost: {book.cost}</p>
            </li>
          ))}
        </ul>
      )}
      <div className="best-deal-button">
        <button onClick={() => onBestDealClick(storeName)}>Best Deal</button>
      </div>
    </div>
  );
};

BookStore.propTypes = {
  storeName: PropTypes.string.isRequired,
  books: PropTypes.arrayOf(
    PropTypes.shape({
      bookName: PropTypes.string.isRequired,
      author: PropTypes.string.isRequired,
      cost: PropTypes.number.isRequired,
    })
  ).isRequired,
  onBestDealClick: PropTypes.func.isRequired,
};

export default BookStore;
