import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [selectedBook, setSelectedBook] = useState('');
  const [selectedType, setSelectedType] = useState('');
  const [bestDeal, setBestDeal] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const findBestDeal = async () => {
    try {
      if (!selectedBook || !selectedType) {
        setErrorMessage('Please select a book and type');
        return;
      }

      const response = await axios.get(`http://localhost:8086/virtualstore/book?name=${encodeURIComponent(selectedBook)}&model=${selectedType}`);

      // Extract relevant data from response
      const { timeStatistics, bestPriceDeal, allDeals } = response.data;

      // Update state with structured data
      setBestDeal({
        timeStatistics,
        bestPriceDeal,
        allDeals
      });

      setErrorMessage(''); // Clear error message on successful fetch
    } catch (error) {
      if (error.response) {
        console.error('Error response from server:', error.response.data);
        setErrorMessage(`Error: ${error.response.data.message}`);
      } else if (error.request) {
        console.error('No response received:', error.request);
        setErrorMessage('No response received from server');
      } else {
        console.error('Error setting up request:', error.message);
        setErrorMessage('Error setting up request');
      }
    }
  };

  return (
    <div className="App">
      <h1>Find Best Book Deal</h1>
      <div>
        <label>Select Book:</label>
        <select value={selectedBook} onChange={(e) => setSelectedBook(e.target.value)}>
          <option value="">-- Select Book --</option>
          {/* Populate options based on your book list */}
          <option value="The Lord of the Rings">The Lord of the Rings</option>
          {/* Add other book options as needed */}
        </select>
      </div>
      <div>
        <label>Select Type:</label>
        <select value={selectedType} onChange={(e) => setSelectedType(e.target.value)}>
          <option value="">-- Select Type --</option>
          <option value="virtual">Virtual</option>
          <option value="traditional">Traditional</option>
        </select>
      </div>
      <button onClick={findBestDeal}>Find Best Deal</button>
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      {bestDeal && (
        <div className="best-deal">
          <h2>Best Deal</h2>
          <p>Book Name: {bestDeal.bestPriceDeal.bookName}</p>
          <p>Author: {bestDeal.bestPriceDeal.author}</p>
          <p>Store: {bestDeal.bestPriceDeal.bookStore}</p>
          <p>Price: ${bestDeal.bestPriceDeal.cost}</p>
          {/* Additional logic to display all deals */}
          <h3>All Deals</h3>
          <ul>
            {bestDeal.allDeals.map((deal, index) => (
              <li key={index}>
                <p>Store: {deal.bookStore}</p>
                <p>Book Name: {deal.bookName}</p>
                <p>Author: {deal.author}</p>
                <p>Price: ${deal.cost}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
