import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import BookStore from './components/BookStore';
import './App.css';

function App() {
  const [bookStores, setBookStores] = useState([]);
  const [bestDeal, setBestDeal] = useState(null);

  useEffect(() => {
    fetch('https://your.mock.server.endpoint')
      .then(response => response.json())
      .then(data => setBookStores(data))
      .catch(error => console.error('Error fetching book stores:', error));
  }, []);

  const fetchBestDeal = () => {
    fetch('http://localhost:8086/virtualstore/book?name=BookName')
      .then(response => response.json())
      .then(data => setBestDeal(data))
      .catch(error => console.error('Error fetching best deal:', error));
  };

  return (
    <div>
      <Header />
      <div className="book-stores">
        {bookStores.map((store, index) => (
          <BookStore key={index} name={store.name} books={store.books} />
        ))}
      </div>
      <div className="best-deal-button">
        <button onClick={fetchBestDeal}>Get Best Deal</button>
      </div>
      {bestDeal && (
        <div className="best-deal">
          <h2>Best Deal</h2>
          <p>Store: {bestDeal.bestPriceDeal.bookStore}</p>
          <p>Book: {bestDeal.bestPriceDeal.bookName}</p>
          <p>Author: {bestDeal.bestPriceDeal.author}</p>
          <p>Cost: {bestDeal.bestPriceDeal.cost}</p>
        </div>
      )}
      <Footer />
    </div>
  );
}

export default App;
