import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import BookStore from './components/BookStore';
import Footer from './components/Footer';
import axios from 'axios';

const App = () => {
  const [bookStores, setBookStores] = useState([]);
  const [bestDeal, setBestDeal] = useState(null);

  useEffect(() => {
    fetchBookStores();
  }, []);

  const fetchBookStores = async () => {
    try {
      const response = await axios.get('https://bcc02357-6483-42cb-9757-d848d27d3480.mock.pstmn.io/store/book');
      // Assuming the response.data is an array of books grouped by store
      const groupedBooks = groupBooksByStore(response.data);
      setBookStores(groupedBooks);
    } catch (error) {
      console.error('Error fetching book stores:', error);
    }
  };

  const groupBooksByStore = (books) => {
    const grouped = {};
    books.forEach(book => {
      if (!grouped[book.bookStore]) {
        grouped[book.bookStore] = [];
      }
      grouped[book.bookStore].push(book);
    });
    return Object.keys(grouped).map(storeName => ({ name: storeName, books: grouped[storeName] }));
  };

  const findBestDeal = async () => {
    try {
      const response = await axios.get('http://localhost:8086/virtualstore/book', {
        params: { name: 'The Lord of the Rings' } // Change this to the book name you want to search for
      });
      setBestDeal(response.data);
    } catch (error) {
      console.error('Error fetching best deal:', error);
    }
  };

  return (
    <div>
      <Header />
      <div className="book-stores">
        {bookStores.map((store, index) => (
          <BookStore key={index} name={store.name} books={store.books} />
        ))}
      </div>
      <div className="best-deal-button">
        <button onClick={findBestDeal}>Best Deal Price</button>
      </div>
      {bestDeal && (
        <div className="best-deal">
          <h2>Best Deal</h2>
          <p>Book Name: {bestDeal.bestPriceDeal.bookName}</p>
          <p>Author: {bestDeal.bestPriceDeal.author}</p>
          <p>Store: {bestDeal.bestPriceDeal.bookStore}</p>
          <p>Price: ${bestDeal.bestPriceDeal.cost}</p>
        </div>
      )}
      <Footer />
    </div>
  );
};

export default App;
